package com.poorna.recyclerviewwithswiperefresher.dao

import com.poorna.recyclerviewwithswiperefresher.entity.BusinessMan

class BusinessManProvider {

    companion object {

        fun getBusinessMen(): ArrayList<BusinessMan> {

            val businessMen = ArrayList<BusinessMan>()

            businessMen.add(BusinessMan("Poorna", "Jayasinghe", 28, "https://avatars0.githubusercontent.com/u/26864215?s=460&v=4"))
            businessMen.add(BusinessMan("Elon", "Musk", 48, "https://www.biography.com/.image/t_share/MTY2MzU3Nzk2OTM2MjMwNTkx/elon_musk_royal_society.jpg"))
            businessMen.add(BusinessMan("Bill", "Gates", 52, "https://specials-images.forbesimg.com/imageserve/5c76b4b84bbe6f24ad99c370/416x416.jpg?background=000000&cropX1=0&cropX2=4000&cropY1=0&cropY2=4000"))
            businessMen.add(BusinessMan("Jeff", "Besoz", 51, "http://www.gstatic.com/tv/thumb/persons/532529/532529_v9_bb.jpg"))
            businessMen.add(BusinessMan("Andrew", "Carnegie", 120, "https://www.biography.com/.image/t_share/MTE1ODA0OTcxNTc1NTc2MDc3/andrew-carnegie-9238756-1-402.jpg"))
            businessMen.add(BusinessMan("Henry", "Ford", 100, "data:image/jpeg;base64,"))
            businessMen.add(BusinessMan("John D", "Rockerfeller", 99, "https://www-tc.pbs.org/wgbh/americanexperience/media/__sized__/canonical_images/feature/Rockefeller_John_Sr_canonical-resize-1200x0-50.jpg"))

            return businessMen
        }

        fun getTenRandomBusinessMen(): ArrayList<BusinessMan> {
            val businessMen = getBusinessMen()
            val randomBusinessMen = ArrayList<BusinessMan>()

            for (i in 0..10) {
                val rand = (0..4).random()
                randomBusinessMen.add(businessMen.get(rand))
            }

            return randomBusinessMen
        }
    }
}